s_seq;INSERT INTO 	MP_USERS(				                USERID,                SITEID,                NAME,                LOGINNAME,                EMAIL,                LOWEREDEMAIL,                GENDER,                ACCOUNTAPPROVED,                REGISTERCONFIRMGUID,                TRUSTED,                DISPLAYINMEMBERLIST,                WEBSITEURL,                COUNTRY,                STATE,                AVATARURL,                SIGNATURE,                DATECREATED,                USERGUID,                ISDELETED,                FAILEDPASSWORDATTEMPTCOUNT,                FAILEDPWDANSWERATTEMPTCOUNT,                ISLOCKEDOUT,                COMMENT,                SITEGUID,				MustChangePwd,				FIRSTNAME,				LASTNAME,				EMAILCHANGEGUID,				TIMEZONEID,				PasswordResetGuid,				RolesChanged,				DateOfBirth,				EmailConfirmed,				PasswordHash,				SecurityStamp,				PhoneNumber,				PhoneNumberConfirmed,				TwoFactorEnabled,				LockoutEndDateUtc,				AuthorBio) VALUES (				               :USERID,               :SITEID,               :NAME,               :LOGINNAME,               :EMAIL,               :LOWEREDEMAIL,               :GENDER,               :ACCOUNTAPPROVED,               :REGISTERCONFIRMGUID,               :TRUSTED,               :DISPLAYINMEMBERLIST,               :WEBSITEURL,               :COUNTRY,               :STATE,               :AVATARURL,               :SIGNATURE,               :DATECREATED,               :USERGUID,               :ISDELETED,               :FAILEDPASSWORDATTEMPTCOUNT,               :FAILEDPWDANSWERATTEMPTCOUNT,               :ISLOCKEDOUT,               :COMMENT,               :SITEGUID,				:MustChangePwd,				:FIRSTNAME,				:LASTNAME,				:EMAILCHANGEGUID,				:TIMEZONEID,				'00000000-0000-0000-0000-000000000000',				0,				:DateOfBirth,				:EmailConfirmed,				:PasswordHash,				:SecurityStamp,				:PhoneNumber,				:PhoneNumberConfirmed,				:TwoFactorEnabled,				:LockoutEndDateUtc,				:AuthorBio								);END^SET TERM ; ^